import { useState } from 'react';

function AddItemBar({ itemText, onItemTextChange, onAdd }) {
  return (
    <form>
      <input
        type="text"
        value={itemText}
        placeholder="add todo..."
        onChange={(e) => onItemTextChange(e.target.value)}
      />
      <button onClick={() => onAdd()}>ADD</button>
    </form>
  );
}

export default function ToDoList() {
  const [itemText, setItemText] = useState("");
  const [todoItems, setToDoItems] = useState([]);

  function handleAddItem() {
    const nextTodoItems = [...todoItems.slice(), itemText];
    setToDoItems(nextTodoItems);
  }

  function updateItemText(newItem) {
    setItemText(newItem);
  }
  const items = todoItems.map((oneitem, i) => {
    return <li key={i}>{oneitem}</li>;
  });

  return (
    <div className="todolist">
      <div className="addtodo">
        <AddItemBar
          itemText={itemText}
          onItemTextChange={updateItemText}
          onAdd={handleAddItem}
        />
      </div>
      <div className="todoitems">
        <ol>{items}</ol>
      </div>
    </div>
  );
}
