import { useState } from 'react';

function readInputAsLines(filename) {
  try {
    const fs = require("fs");
    const data = fs.readFileSync(filename, "utf8");
    const lines = data.split("\n");
    return lines;
  } catch (err) {
    console.error(err);
  }
  return [];
}

export default function ToDoList() {
  const [itemText, setItemText] = useState("");
  const [todoItems, setToDoItems] = useState([]);

  function handleAddItem(newItem) {
    if (!newItem) {
      return;
    }
    const nextTodoItems = [...todoItems.slice(), newItem];
    setToDoItems(nextTodoItems);
    setItemText("");
  }

  function onItemTextChange(newItem) {
    setItemText(newItem);
  }
  const items = [];
  readInputAsLines("input.txt").forEach((line, i) => {
    if (line) {
      items.push(<li key={1000 + i}>{line}</li>);
    }
  });
  todoItems.forEach((oneitem, i) => {
    items.push(<li key={i}>{oneitem}</li>);
  });

  return (
    <div className="todolist">
      <div className="addtodo">
        <form
          onSubmit={(e) => {
            e.preventDefault();
            handleAddItem(itemText);
          }}
        >
          <input
            type="text"
            value={itemText}
            placeholder="add todo..."
            onChange={(e) => onItemTextChange(e.target.value)}
          />
          <input type="submit" value="ADD" />
        </form>
      </div>
      <div className="todoitems">
        <ol>{items}</ol>
      </div>
    </div>
  );
}
